mapHistoryDataToExecLog(historyData, result);

function getQueryFilter(refKey) {
    var identityFilter = {
        "fieldName": "ReferenceKey",
        "type": "EQ",
        "value": refKey
    };
    var typeFilter = {
        "fieldName": "DataShapeName",
        "type": "EQ",
        "value": "PTC.SCA.SCO.WorkDefinition"
    };
    var statusChangeFilter = {
        "fieldName": "FieldName",
        "type": "EQ",
        "value": "ExecutionStatus"
    };
    var sortConfig = {
        "fieldName": "TimeStamp",
        "isAscending": true,
        "isCaseSensitive": false
    };

    return {
        "filters": {
            "filters": [identityFilter, typeFilter, statusChangeFilter],
            "type": "AND"
        },
        "sorts": [sortConfig]
    };
}

function mapHistoryDataToExecLog(historyData, execLog) {
    var execStatusTable = me.GetWorkDefinitionExecutionStatuses();
    for (var i=0; i < historyData.rows.length; i++) {
        var historyDataRow = historyData.rows[i];
        var execLogEntry = new Object();
        execLogEntry.UserName = historyDataRow.UserName;
        if (historyDataRow.Value) {
            execLogEntry.StateUID = historyDataRow.Value;
            execLogEntry.StateName = getExecStateName(execStatusTable, historyDataRow.Value);
        }
        execLogEntry.TimeStamp = historyDataRow.TimeStamp;
        execLog.AddRow(execLogEntry);
    }
}

function getExecStateName(execStatusTable, execStatusUID) {
    var execStatusName;
    var execStatus = execStatusTable.Find({ UID: execStatusUID });
    if (execStatus) {
        execStatusName = execStatus.Name;
    } else {
        logger.error("Cannot find execution status for UID '" + execStatusUID + "'");
    }
    return execStatusName;
}


var execLog = me.GetWorkDefinitionExecutionLog({ WorkDefinitionUID: WorkDefinitionUID });
var result = populateExecSummary(execLog, initExecSummary(), IncludeCurrentStatus);

function initExecSummary() {
    var dataShapeParams = {
        infoTableName : "InfoTable",
        dataShapeName : "PTC.SCA.SCO.WorkDefinitionExecutionStatusSummary"
    };
    var execSummary = Resources["InfoTableFunctions"].CreateInfoTableFromDataShape(dataShapeParams);
    var execStatusList = me.GetWorkDefinitionExecutionStatuses();
    for (var i=0; i < execStatusList.rows.length; i++) {
        var execStatus = execStatusList.rows[i];
        execSummary.AddRow({ "StateUID": execStatus.UID, "StateName": execStatus.Name, "TotalTime": 0 });
    }
    return execSummary;
}

function populateExecSummary(execLog, execSummary, includeCurrentStatus) {
    if (needsExecSummaryUpdate(execLog)) {
        for (var i=0; i < execLog.rows.length; i++) {
            if (i < execLog.rows.length - 1) {
                updateTotalTime(execSummary, execLog.rows[i].StateUID, execLog.rows[i].TimeStamp, execLog.rows[i+1].TimeStamp)
            } else if (includeCurrentStatus && i == execLog.rows.length - 1) {
                updateTotalTime(execSummary, execLog.rows[i].StateUID, execLog.rows[i].TimeStamp, Date.now())
            }
        }
        convertTotalTimeToSec(execSummary);
    }
    return execSummary;
}

function needsExecSummaryUpdate(execLog) {
    return 1 < execLog.rows.length || 1 == execLog.rows.length && execLog.rows[0].StateUID;
}

function updateTotalTime(execSummary, stateUID, beginTimeStamp, endTimeStamp) {
    var execStat = execSummary.Find({ StateUID: stateUID });
    execStat.TotalTime += (endTimeStamp - beginTimeStamp);
}

function convertTotalTimeToSec(execSummary) {
    for (var i=0; i < execSummary.rows.length; i++) {
        if (execSummary.rows[i].TotalTime) {
            execSummary.rows[i].TotalTime /= 1000
        }
    }
}


